CREATE TABLE `testclub`.`airports` (  `id_airport` VARCHAR(45) NOT NULL,  `dutyFree` VARCHAR(45) NOT NULL,  `priorityBoarding` VARCHAR(45) NOT NULL,  PRIMARY KEY (`id_airport`));
 CREATE TABLE `testclub`.`flight` (  `flightNumber` INT NOT NULL,  `departureAirport` VARCHAR(45) NOT NULL,  `arrivalAirport` VARCHAR(45) NOT NULL,  `stopsNumber` INT NOT NULL,  `averageTicketPrice` INT NOT NULL,  `availableSeats` INT NOT NULL,  PRIMARY KEY (`flightNumber`));
CREATE TABLE `testclub`.`airlanes` (  `flightNumber` INT NOT NULL,  `airline` VARCHAR(45) NOT NULL,  `additionalSpaceService` VARCHAR(45) NOT NULL,  `webRegistration` VARCHAR(45) NOT NULL,  `isMealincluded` VARCHAR(45) NOT NULL,  PRIMARY KEY (`flightNumber`));
